// Примерен минимизиран JavaScript за интегриране на QR код четец
// Можете да използвате библиотеката html5-qrcode

// Включете връзката към библиотеката html5-qrcode в HTML файловете
// Например:
// <script src="/static/js/html5-qrcode.min.js"></script>

// Примерна инициализация на QR код четец
function onScanSuccess(decodedText, decodedResult) {
    console.log(`Scan result: ${decodedText}`, decodedResult);
}

function onScanFailure(error) {
    console.warn(`Scan failure: ${error}`);
}

let html5QrcodeScanner = new Html5QrcodeScanner("reader", { fps: 10, qrbox: 250 });
html5QrcodeScanner.render(onScanSuccess, onScanFailure);
